$(document).ready(function() {
    $(window).load(function(a) {
        positionFixedItems();
    }), $(window).on("resizeEnd", function(a) {
        positionFixedItems();
    }), window.positionFixedItems = function() {
        $(window).width() > 1024 ? cssObj = {
            left: "50%",
            "margin-left": "-" + $("header").width() / 2 + "px"
        } : cssObj = {
            left: "0",
            "margin-left": "0px"
        }, $("header").css(cssObj);
    };
});